import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Button from "../components/button";

export default function Login() {
  const [mobile, setMobile] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [showPassword, setShowPassword] = useState(false);
  const [shake, setShake] = useState(false);
  const [currentTheme, setCurrentTheme] = useState("light");
  const navigate = useNavigate();

  // Dynamic background themes
  const themes = {
    light: "bg-gradient-to-br from-gray-100 to-gray-200",
    sunset: "bg-gradient-to-br from-yellow-100 via-orange-200 to-pink-200",
    ocean: "bg-gradient-to-br from-blue-50 to-teal-100",
    forest: "bg-gradient-to-br from-green-50 to-emerald-100"
  };

  // Rotate themes every 10 seconds
  useEffect(() => {
    const themeKeys = Object.keys(themes);
    let currentIndex = 0;
    
    const interval = setInterval(() => {
      currentIndex = (currentIndex + 1) % themeKeys.length;
      setCurrentTheme(themeKeys[currentIndex]);
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);
    
    try {
      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 1500));
      
      // Replace with actual API call in production
      if (mobile === "1234567890" && password === "password") {
        navigate("/dashboard");
      } else {
        setError("Invalid mobile number or password");
        setShake(true);
        setTimeout(() => setShake(false), 500);
      }
    } catch (err) {
      setError("An error occurred. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  return (
    <div className={`flex items-center justify-center min-h-screen ${themes[currentTheme]} px-4 transition-colors duration-1000`}>
      <div className={`bg-white p-8 rounded-xl shadow-xl w-full max-w-md transform transition-all ${shake ? "animate-shake" : ""}`}>
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-gray-800 mb-2">Welcome Back!</h2>
          <p className="text-gray-600">Login to access your BetX account</p>
        </div>

        <form onSubmit={handleLogin} className="space-y-6">
          {error && (
            <div className="bg-red-50 border-l-4 border-red-500 p-4 text-red-700">
              <p>{error}</p>
            </div>
          )}

          <div className="space-y-1">
            <label htmlFor="mobile" className="block font-medium text-gray-700">
              Mobile Number
            </label>
            <div className="relative">
              <input
                type="text"
                id="mobile"
                value={mobile}
                onChange={(e) => setMobile(e.target.value)}
                className="w-full border border-gray-300 rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all"
                placeholder="Enter your mobile number"
                required
              />
              <span className="absolute right-3 top-3 text-gray-400">üì±</span>
            </div>
          </div>

          <div className="space-y-1">
            <label htmlFor="password" className="block font-medium text-gray-700">
              Password
            </label>
            <div className="relative">
              <input
                type={showPassword ? "text" : "password"}
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full border border-gray-300 rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all"
                placeholder="Enter your password"
                required
              />
              <button
                type="button"
                onClick={togglePasswordVisibility}
                className="absolute right-3 top-3 text-gray-400 hover:text-gray-600 focus:outline-none"
              >
                {showPassword ? "üëÅÔ∏è" : "üëÅÔ∏è‚Äçüó®Ô∏è"}
              </button>
            </div>
            <div className="flex justify-end">
              <a href="/forgot-password" className="text-sm text-yellow-600 hover:underline">
                Forgot password?
              </a>
            </div>
          </div>

          <Button 
            type="submit" 
            className="w-full py-3 flex items-center justify-center"
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                </svg>
                Logging in...
              </>
            ) : (
              "Login"
            )}
          </Button>
        </form>

        <div className="mt-8 text-center">
          <p className="text-gray-600">
            Don't have an account?{" "}
            <a href="/register" className="text-yellow-600 font-medium hover:underline">
              Create one
            </a>
          </p>
        </div>

        <div className="mt-6 flex justify-center space-x-4">
          <button className="p-2 rounded-full border border-gray-200 hover:bg-gray-50 transition-colors">
            <span className="sr-only">Login with Google</span>
            <span aria-hidden="true">G</span>
          </button>
          <button className="p-2 rounded-full border border-gray-200 hover:bg-gray-50 transition-colors">
            <span className="sr-only">Login with Facebook</span>
            <span aria-hidden="true">f</span>
          </button>
          <button className="p-2 rounded-full border border-gray-200 hover:bg-gray-50 transition-colors">
            <span className="sr-only">Login with Twitter</span>
            <span aria-hidden="true">t</span>
          </button>
        </div>
      </div>
    </div>
  );
}
