import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Button from "../components/button";

export default function Login() {
  const [mobile, setMobile] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    // TODO: Replace with actual login logic or API call
    if (mobile === "1234567890" && password === "password") {
      alert("Login successful!");
      navigate("/dashboard");
    } else {
      alert("Invalid credentials");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 px-4">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-md">
        <h2 className="text-2xl font-bold mb-6 text-center text-gray-800">Login to BetX</h2>
        <form onSubmit={handleLogin} className="space-y-4">
          <div>
            <label htmlFor="mobile" className="block mb-1 font-medium text-gray-700">
              Mobile Number
            </label>
            <input
              type="text"
              id="mobile"
              value={mobile}
              onChange={(e) => setMobile(e.target.value)}
              className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
              placeholder="Enter mobile number"
              required
            />
          </div>

          <div>
            <label htmlFor="password" className="block mb-1 font-medium text-gray-700">
              Password
            </label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full border border-gray-300 rounded px-3 py-2 focus:outline-none focus:ring-2 focus:ring-yellow-400"
              placeholder="Enter password"
              required
            />
          </div>

          <Button type="submit" className="w-full">Login</Button>
        </form>

        <p className="mt-4 text-center text-sm text-gray-600">
          Donâ€™t have an account?{" "}
          <a href="/register" className="text-yellow-500 hover:underline">
            Register
          </a>
        </p>
      </div>
    </div>
  );
}
