import React from "react";
import { Link } from "react-router-dom";
import Button from "../components/button";

const games = [
  { name: "Aviator", path: "/aviator", image: "/assets/aviator.jpg" },
  { name: "CrashX", path: "/crashx", image: "/assets/crashx.jpg" },
  { name: "Spin & Win", path: "/spin", image: "/assets/spin.jpg" },
  { name: "Dice Roll", path: "/dice", image: "/assets/dice.jpg" },
];

export default function Home() {
  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white p-6 hidden sm:block">
        <h2 className="text-2xl font-bold mb-6">ðŸŽ® BetX Games</h2>
        <nav className="space-y-4">
          {games.map((game) => (
            <Link
              key={game.name}
              to={game.path}
              className="block py-2 px-4 rounded hover:bg-gray-700 transition"
            >
              {game.name}
            </Link>
          ))}
        </nav>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-6">
        <h1 className="text-3xl font-bold mb-6">Play Now</h1>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-2 gap-6">
          {games.map((game) => (
            <div
              key={game.name}
              className="bg-white rounded-lg shadow-md p-4 flex flex-col items-center"
            >
              <img
                src={game.image}
                alt={game.name}
                className="w-full h-40 object-cover rounded mb-4"
              />
              <h2 className="text-xl font-semibold mb-2">{game.name}</h2>
              <Link to={game.path}>
                <Button>Play Now</Button>
              </Link>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
