import React, { useState, useEffect,useCallback } from "react";
import { GameLayout } from "../../components/gamelayout.jsx";
import { BetControl } from "../../components/betcontrol.jsx";
import "../css/spinandwin.css";

export const SpinAndWin = ({ balance, onBack, onBalanceChange }) => {
  const symbols = ["🍒", "🍋", "🍊", "🍇", "🔔", "⭐", "7️⃣", "💰"];
  const [wheels, setwheels] = useState([0, 0, 0]);
  const [spinning, setSpinning] = useState(false);
  const [winAmount, setWinAmount] = useState(0);
  const [history, setHistory] = useState([]);
  const [autoSpins, setAutoSpins] = useState(0);
  const [jackpot, setJackpot] = useState(10000);
  const [showWinAnimation, setShowWinAnimation] = useState(false);
  const [currentBet, setCurrentBet] = useState(0);

  // Paytable configuration
  const paytable = {
    "7️⃣7️⃣7️⃣": { multiplier: 20, bonus: "JACKPOT" },
    "💰💰💰": { multiplier: 10, bonus: "BIG WIN" },
    "🔔🔔🔔": { multiplier: 5 },
    "⭐️⭐️⭐️": { multiplier: 5 },
    "🍇🍇🍇": { multiplier: 3 },
    "🍊🍊🍊": { multiplier: 2 },
    "🍋🍋🍋": { multiplier: 2 },
    "🍒🍒🍒": { multiplier: 2 },
    "🍒🍒": { multiplier: 1.5 },
    "🍋🍋": { multiplier: 1.2 },
  };

  const spinwheels = useCallback(() => {
    setSpinning(true);
    setWinAmount(0);
    
    // Deduct the bet amount
    onBalanceChange(-currentBet);
    
    // Generate random stops for each reel
    const spins = wheels.map(() => Math.floor(Math.random() * symbols.length));
    
    // Animate each reel with different durations
    const spinDurations = [1000, 1500, 2000];
    
    spins.forEach((stop, index) => {
      const duration = spinDurations[index];
      const startTime = Date.now();
      
      const spinInterval = setInterval(() => {
        const elapsed = Date.now() - startTime;
        const progress = Math.min(elapsed / duration, 1);
        
        // Update reel position during spin
        setwheels(prev => {
          const newwheels = [...prev];
          newwheels[index] = (newwheels[index] + 1) % symbols.length;
          return newwheels;
        });
        
        if (progress >= 1) {
          clearInterval(spinInterval);
          // Set final position
          setwheels(prev => {
            const newwheels = [...prev];
            newwheels[index] = stop;
            return newwheels;
          });
          
          // Check if all wheels have stopped
          if (index === spins.length - 1) {
            setTimeout(() => finishSpin(stops), 500);
          }
        }
      }, 50);
    });
  }, [currentBet, onBalanceChange, wheels, symbols.length]);

  const finishSpin = (stops) => {
    setSpinning(false);
    
    // Get the symbols that landed
    const result = stops.map(stop => symbols[stop]).join("");
    
    // Check for winning combinations
    let win = 0;
    let bonus = null;
    
    // Check for three matching symbols
    if (result[0] === result[1] && result[1] === result[2]) {
      const combo = `${result[0]}${result[1]}${result[2]}`;
      if (paytable[combo]) {
        win = currentBet * paytable[combo].multiplier;
        bonus = paytable[combo].bonus;
      }
    }
    // Check for two matching symbols (only left two)
    else if (result[0] === result[1]) {
      const combo = `${result[0]}${result[1]}`;
      if (paytable[combo]) {
        win = currentBet * paytable[combo].multiplier;
      }
    }
    
    // Handle jackpot
    if (bonus === "JACKPOT") {
      win += jackpot;
      setJackpot(10000); // Reset jackpot
    } else if (result === "💰💰💰") {
      setJackpot(prev => prev + currentBet * 5);
    }
    
    setWinAmount(win);
    if (win > 0) {
      onBalanceChange(win);
      setShowWinAnimation(true);
      setTimeout(() => setShowWinAnimation(false), 2000);
    }
    
    // Add to history
    setHistory(prev => [
      { result, win, timestamp: new Date(), bonus },
      ...prev.slice(0, 9)
    ]);
    
    // Continue auto spins if enabled
    if (autoSpins > 1) {
      setTimeout(() => {
        setAutoSpins(autoSpins - 1);
        spinwheels();
      }, 1500);
    }
  };

  const handleBet = ({ amount, autoRoll, rollsCount }) => {
    if (amount > balance) return;
    
    setCurrentBet(amount);
    
    if (autoRoll) {
      setAutoSpins(rollsCount);
    }
    
    spinwheels();
  };

  return (
    <GameLayout title="SpinAndWin" balance={balance} onBack={onBack}>
      <div className="slot-machine-container">
        <div className="jackpot-display">
          <div className="jackpot-label">JACKPOT</div>
          <div className="jackpot-amount">₹{jackpot.toLocaleString()}</div>
        </div>
        
        <div className="slot-wheels">
          {wheels.map((reel, index) => (
            <div key={index} className={`reel ${spinning ? "spinning" : ""}`}>
              <div className="reel-strip">
                {[...symbols, ...symbols, ...symbols].map((symbol, i) => (
                  <div 
                    key={i} 
                    className={`symbol ${i % symbols.length === reel ? "center" : ""}`}
                  >
                    {symbol}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
        
        <div className="payline"></div>
        
        {showWinAnimation && (
  <div className="win-animation">
    {winAmount >= jackpot ? (
      <div className="jackpot-win">JACKPOT WIN!</div>
    ) : winAmount > currentBet * 5 ? (
      <div className="big-win">BIG WINNER!</div>
    ) : (
      <div className="win">YOU WIN!</div>
    )}
  </div>
)}
        
        <div className="last-spin">
          {history.length > 0 && !spinning && (
            <div className={`result ${history[0].win > 0 ? "win" : ""}`}>
              {history[0].result} {history[0].win > 0 ? `+₹${history[0].win}` : ""}
            </div>
          )}
        </div>

        <button 
  className="big-spin-button"
  onClick={() => handleBet({ amount: currentBet })}
  disabled={spinning}
>
  SPIN NOW
</button>
        
        <BetControl 
  buttonText={autoSpins > 0 ? `Auto Spin (${autoSpins}x)` : "Spin Now"}
/>
        <div className="paytable">
          <h3>Paytable</h3>
          <div className="paytable-grid">
            {Object.entries(paytable).map(([combo, { multiplier, bonus }]) => (
              <div key={combo} className="paytable-item">
                <div className="combo">{combo}</div>
                <div className="payout">{multiplier}x</div>
                {bonus && <div className="bonus">{bonus}</div>}
              </div>
            ))}
          </div>
        </div>
        
        <div className="spin-history">
          <h3>Recent Spins</h3>
          <div className="history-list">
            {history.length > 0 ? (
              history.map((spin, index) => (
                <div 
                  key={index} 
                  className={`history-item ${spin.win > 0 ? "win" : ""}`}
                >
                  <span className="result">{spin.result}</span>
                  <span className="time">
                    {spin.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </span>
                  <span className="amount">
                    {spin.win > 0 ? `+₹${spin.win}` : "-"}
                  </span>
                  {spin.bonus && <span className="bonus">{spin.bonus}</span>}
                </div>
              ))
            ) : (
              <div className="no-history">No spins yet</div>
            )}
          </div>
        </div>
      </div>
    </GameLayout>
  );
};
