import React, { useState, useEffect } from "react";
import { GameLayout } from "../../components/gamelayout";
import { BetControl } from "../../components/betcontrol";
import "../css/spinandwin.css";

export const SpinAndWin = ({ balance, onBack, onBalanceChange }) => {
  const [spinning, setSpinning] = useState(false);
  const [result, setResult] = useState(null);
  const [winAmount, setWinAmount] = useState(0);
  const [history, setHistory] = useState([]);
  const [autoSpinning, setAutoSpinning] = useState(false);
  const [spinsLeft, setSpinsLeft] = useState(0);

  const prizes = [
    { id: 1, name: "2x", multiplier: 2, color: "#FF5733" },
    { id: 2, name: "1.5x", multiplier: 1.5, color: "#33FF57" },
    { id: 3, name: "Lose", multiplier: 0, color: "#3357FF" },
    { id: 4, name: "3x", multiplier: 3, color: "#F033FF" },
    { id: 5, name: "0.5x", multiplier: 0.5, color: "#FF33F0" },
    { id: 6, name: "5x", multiplier: 5, color: "#33FFF0" },
    { id: 7, name: "Lose", multiplier: 0, color: "#F0FF33" },
    { id: 8, name: "10x", multiplier: 10, color: "#FF8C33" },
  ];

  const spinWheel = () => {
    const spinDuration = 3000; // 3 seconds
    const startTime = Date.now();
    const spinInterval = setInterval(() => {
      const elapsed = Date.now() - startTime;
      const progress = Math.min(elapsed / spinDuration, 1);
      const easedProgress = 1 - Math.pow(1 - progress, 3); // Easing function
      
      const randomIndex = Math.floor(Math.random() * prizes.length);
      setResult(prizes[randomIndex]);

      if (progress >= 1) {
        clearInterval(spinInterval);
        finishSpin(prizes[randomIndex]);
      }
    }, 50);
  };

  const finishSpin = (prize) => {
    setSpinning(false);
    setResult(prize);
    
    if (autoSpinning && spinsLeft > 1) {
      setTimeout(() => {
        setSpinsLeft(spinsLeft - 1);
        handleSpin();
      }, 1500);
    } else {
      setAutoSpinning(false);
    }
  };

  const handleBet = ({ amount, autoRoll, rollsCount }) => {
    if (amount > balance) return;
    
    if (autoRoll) {
      setAutoSpinning(true);
      setSpinsLeft(rollsCount);
      handleSpin(amount);
      return;
    }
    
    handleSpin(amount);
  };

  const handleSpin = (amount) => {
    if (spinning) return;
    
    setSpinning(true);
    onBalanceChange(-amount);
    
    // Store the current bet amount for the result calculation
    setCurrentBet(amount);
    spinWheel();
  };

  const [currentBet, setCurrentBet] = useState(0);

  useEffect(() => {
    if (result && !spinning) {
      const win = currentBet * result.multiplier;
      setWinAmount(win);
      if (win > 0) {
        onBalanceChange(win);
      }
      
      setHistory(prev => [
        { prize: result, amount: win, timestamp: new Date() },
        ...prev.slice(0, 9)
      ]);
    }
  }, [result, spinning]);

  return (
    <GameLayout title="Spin & Win" balance={balance} onBack={onBack}>
      <div className="spin-game-container">
        <div className="wheel-container">
          <div className={`wheel ${spinning ? "spinning" : ""}`}>
            {prizes.map((prize, index) => (
              <div 
                key={prize.id}
                className="wheel-segment"
                style={{
                  transform: `rotate(${index * (360 / prizes.length)}deg)`,
                  backgroundColor: prize.color,
                  borderLeftColor: prize.color,
                }}
              >
                <span className="prize-name">{prize.name}</span>
              </div>
            ))}
            <div className="wheel-center"></div>
          </div>
          <div className="wheel-pointer"></div>
          
          {result && !spinning && (
            <div className="result-display" style={{ backgroundColor: result.color }}>
              {result.multiplier > 0 ? (
                <div className="win-result">
                  <div>You won {result.multiplier}x!</div>
                  <div className="win-amount">+₹{winAmount.toLocaleString()}</div>
                </div>
              ) : (
                <div className="lose-result">No win this time</div>
              )}
            </div>
          )}
        </div>

        <BetControl 
          balance={balance} 
          onBet={handleBet} 
          buttonText={autoSpinning ? `Auto Spin (${spinsLeft}x)` : "Spin Now"}
        />

        <div className="spin-history">
          <h3>Recent Spins</h3>
          <div className="history-list">
            {history.length > 0 ? (
              history.map((spin, index) => (
                <div 
                  key={index} 
                  className={`history-item ${spin.amount > 0 ? "win" : "lose"}`}
                  style={{ backgroundColor: spin.prize.color }}
                >
                  <span className="history-prize">{spin.prize.name}</span>
                  <span className="history-time">
                    {spin.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </span>
                  <span className="history-amount">
                    {spin.amount > 0 ? `+₹${spin.amount}` : "-"}
                  </span>
                </div>
              ))
            ) : (
              <div className="no-history">No spins yet</div>
            )}
          </div>
        </div>
      </div>
    </GameLayout>
  );
};
